// AppContent.js
import * as React from 'react';
import { request, setAuthHeader, getAuthToken } from '../helpers/axios_helper';
import Buttons from './Buttons';
import LoginForm from './LoginForm';
import WelcomeContent from './WelcomeContent'
import AuthContent from './AuthContent';
import Factures from './Factures';
import AddReclamation from './AddReclamation';
import FacturesContent from './Factures';
import ReclamationContent from './Reclamation';
import NavBar from '../Layout/NavBar';
import { Route, Router, redirect, unstable_HistoryRouter } from 'react-router-dom';


export default class AppContent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isAuthenticated: Boolean(getAuthToken()) ,
            // Check if the user is initially authenticated
        }
    };


    login = () => {
        this.setState({ componentToShow: "login" });
    };

    logout = () => {
        this.setState({componentToShow: "welcome",  isAuthenticated: false });
        setAuthHeader(null);

    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        request(
            "POST",
            "/login",
            {
                login: username,
                password: password
            }).then(
                (response) => {
                    setAuthHeader(response.data.token);
                    window.localStorage.setItem('cin', response.data.cin);
                    this.setState({componentToShow: "Factures", isAuthenticated: true });


                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({componentToShow: "welcome"});
                    }
                );
    };

    onRegister = (event, firstName, lastName, username, password, cin, tel, adresse) => {
        event.preventDefault();
        console.log("Registering with tel:", tel);
        request(
            "POST",
            "/register",
            {
                firstName: firstName,
                lastName: lastName,
                login: username,
                password: password,
                cin: cin,
                tel: tel,
                adresse: adresse,
            }).then(
                (response) => {
                    setAuthHeader(response.data.token);
                    this.setState({ omponentToShow: "Factures", isAuthenticated: true });
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "welcome", isAuthenticated: false });
                    }
                );
    };

    render() {
        return (
            <Router>
                <>
                    <NavBar login={this.login} logout={this.logout} isAuthenticated={this.state.isAuthenticated} />
                    <Switch>
                        <Route path="/Factures" component={FacturesContent} />
                        <Route path="/welcome" component={WelcomeContent} />
                        <Route
                            path="/login"
                            render={(props) => (
                                <LoginForm {...props} onLogin={this.onLogin} onRegister={this.onRegister} />
                            )}
                        />
                        {/* Redirect to /welcome if authenticated */}
                        {this.state.isAuthenticated && <Redirect from="/login" to="/welcome" />}
                        {/* Add more routes as needed */}
                    </Switch>
                </>
            </Router>
        );
    }
}
