// Factures.js
import React, { useState, useEffect } from 'react';
import { request } from '../helpers/axios_helper';

const Factures = () => {
  const [factures, setFactures] = useState([]);

  useEffect(() => {
    // Fetch factures when the component mounts
    fetchFactures();
  }, []);

  const fetchFactures = () => {
    // Fetch factures based on user's cin
    request('GET', '/api/factures/user-factures')
      .then(response => {
        setFactures(response.data);
      })
      .catch(error => {
        console.error('Error fetching factures:', error);
      });
  };

  return (
    <div>
      <h1>Your Factures</h1>
      <table>
        <thead>
          <tr>
            <th>Num Facture</th>
            <th>Num Police</th>
            {/* Add other facture properties as needed */}
          </tr>
        </thead>
        <tbody>
          {factures.map(facture => (
            <tr key={facture.numFacture}>
              <td>{facture.numFacture}</td>
              <td>{facture.numPolice}</td>
              {/* Add other facture properties as needed */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Factures;
