// NavBar.js
import React from 'react';
import Buttons from './buttons';  // Correct the path if needed
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';

const NavBar = ({ login, logout }) => {
  // Retrieve 'cin' from local storage
  const cinFromLocalStorage = localStorage.getItem('cin');

  return (
    <Navbar expand="lg" className="bg-body-tertiary">
      <Container>
        {/* Check if 'cin' is present in local storage before rendering */}
        {cinFromLocalStorage !== null && cinFromLocalStorage !== undefined && (
          <>
            <Navbar.Brand href="/Welcome">Your Brand Name</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                <Nav.Link href="/Facture">Home</Nav.Link>
                <Nav.Link href="/addReclamation">Link</Nav.Link>
                {/* Add your additional navigation items here */}
              </Nav>
            </Navbar.Collapse>
          </>
        )}
        <Buttons login={login} logout={logout} />
      </Container>
    </Navbar>
  );
};

export default NavBar;
