import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {Link , useParams } from 'react-router-dom';
import { request } from '../helpers/axios_helper';


export default function Factures() {
  
  const [factures, setFactures] = useState([]);  // Initialize as an array
  const { cin } =useParams()

  useEffect(() => {
    loadFactures();
  }, [cin]);  // Add `cin` to the dependency array

  const loadFactures = () => {
    try {
      request('GET', `facture/${cin}`).then(result=>{setFactures(result.data)}) ;
    } catch (error) {
      console.error('Error loading factures:', error);
    }
  };

  return (
    <div className='container'>
      <div className='py-4'>
        <table className="table border shadow">
          <thead>
            <tr>
              <th scope="col">Factures</th>
              <th scope="col">Reference</th>
              <th scope="col">montant</th>
              <th scope="col">etat</th>
              <th scope="col">Date</th>
              <th scope="col">Police</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {factures.map((facture, index) => (
              <tr key={index}>
                <th scope="row">{index + 1}</th>
                <td>{facture.numFacture}</td>
                <td>{facture.montant}</td>
                <td>{facture.etat}</td>
                <td>{facture.dateFacture}</td>
                <td>{facture.numPolice}</td>



                <td>        <Link className='btn btn-outline-primary mx-2'to={`/ViewFacture/${facture.numFacture}`}>view</Link>
</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
