import React, { Component } from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
import { Link } from 'react-router-dom';

export default class UsersContent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    // Retrieve 'cin' from local storage
    const role = localStorage.getItem('role');

    console.log("this is the role", role);

    // Check if 'role' exists before making the request
    if (!role) {
      return;
    }

    request('GET', '/GetUser')
      .then((response) => {
        this.setState({ data: response.data });
        console.log("data:", response.data);
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // Handle unauthorized access
        } else {
          this.setState({ data: [] }); // Set an empty array or handle error appropriately
        }
      });
  }

  render() {
    return (
      <div className="row justify-content-md-center">
        <div className="col-12">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Users</h5>

              <table className="table">
                <thead>
                  <tr>
                    <th>user id</th>
                    <th>First Name</th>
                    <th>Last name</th>
                    <th>Login</th>
                    <th> cin</th>
                    <th>tel</th>
                    <th>adresse</th>
                    <th>role</th>
                    
                  </tr>
                </thead>
                <tbody>
                  {this.state.data &&
                    this.state.data.map((user) => (
                      <tr key={user.numuser}>
                        <td>{user.numuser}</td>
                        <td>{user.numPolice}</td>
                        <td>{user.dateuser}</td>
                        <td>{user.ancienIndex}</td>
                        <td>{user.noveauIndex}</td>
                        <td>{user.prorata}</td>
                        <td>{user.consommation}</td>
                        <td>{user.montant}</td>
                        <td>{user.dateUniteMontant}</td>
                        <td>{user.etat}</td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
