import axios from 'axios'
import * as React from 'react'
import { Link, useNavigate } from 'react-router-dom'

export default class AddReclamation extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        type: "",
        description: "",
        onaddReclamation: props.onaddReclamation
        };
};
  
onChangeHandler = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    this.setState({[name] : value});
};
onSubmitRegister = (e) => {
    this.state.onRegister(e, this.state.type, this.state.description);
};
  render(){
    return(
    <div className='container'>
        <div className='row'>
            <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
                
                <div className={classNames("tab-pane", "fade", this.state.active === "register" ? "show active" : "")} id="pills-register" >
                <form onSubmit={this.onSubmitRegister}>

                  <div className="form-outline mb-4">
                    <input type="text" id="firstName" name="firstName" className="form-control" onChange={this.onChangeHandler}/>
                    <label className="form-label" htmlFor="firstName">First name</label>
                  </div>

                  <div className="form-outline mb-4">
                    <input type="text" id="lastName" name="lastName" className="form-control" onChange={this.onChangeHandler}/>
                    <label className="form-label" htmlFor="lastName">Last name</label>
                  </div>
                <button className='btn btn-outline-primary m-2'>submit</button>
                <Link className='btn btn-outline-danger m-2 ' to="/ ">cancel</Link>
                </form>
            </div>
        </div>

    </div>
    </div>
  );}}
