import axios from 'axios'
import * as React from 'react'
import { Link, useNavigate } from 'react-router-dom'

export default class AddReclamation extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        type: "",
        description: "",
        onaddReclamation: props.onaddReclamation
        };
};
  
onChangeHandler = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    this.setState({[name] : value});
};
onSubmitRegister = (e) => {
    this.state.onRegister(e, this.state.type, this.state.description);
};
  render(){
    return(
    <div className='container'>
        <div className='row'>
            <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
                <h2 className='text-center'>add Reclamation</h2>
                <form onSubmit={(e)=>onSubmit(e)}>
                <div className='mb-4'>
                    <label htmlFor='type'className='form-label'>Type</label>
                    <input className='form-control' type={"text"} placeholder='Enter the type of Reclamation ' name='Reclamation'value={type}  onChange={(e)=>onInputChange(e)}></input>
                </div>
                <div className='mb-4'>
                    <label htmlFor='description'className='form-label'>description</label>
                    <input className='form-control' type={"text"} placeholder='Enter the description of Reclamation ' name='description'value={description}  onChange={(e)=>onInputChange(e)}></input>
                </div>
                <button className='btn btn-outline-primary m-2'>submit</button>
                <Link className='btn btn-outline-danger m-2 ' to="/ ">cancel</Link>
                </form>
            </div>
        </div>

    </div>
  );}}
