import React, { Component } from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
import { Link, useParams } from 'react-router-dom';

export default class UsersContent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      
    };
    
  }
  

  componentDidMount() {
    // Retrieve 'cin' from local storage
    const role = localStorage.getItem('role');
    const {id}=useParams();


    console.log("this is the role", role);

    // Check if 'role' exists before making the request
    if (!role) {
      return;
    }

    request('DELETE', `/User/${id}`)
      .then((response) => {
        this.setState({ data: response.data });
        console.log("data:", response.data);
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // Handle unauthorized access
        } else {
          this.setState({ data: [] }); // Set an empty array or handle error appropriately
        }
      });
  
    request('GET', '/GetUser')
      .then((response) => {
        this.setState({ data: response.data });
        console.log("data:", response.data);
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          // Handle unauthorized access
        } else {
          this.setState({ data: [] }); // Set an empty array or handle error appropriately
        }
      });
  }

  render() {    const role = localStorage.getItem('role');

    return (
      
      <div className="row justify-content-md-center">
        <div className="col-12">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Users</h5>
                {role ==="1" && (

              <table className="table">
                <thead>
                  <tr>
                    <th>user id</th>
                    <th>First Name</th>
                    <th>Last name</th>
                    <th>Login</th>
                    <th> cin</th>
                    <th>tel</th>
                    <th>adresse</th>
                    <th>role</th>
                    <th>Action</th>

                
                  </tr>
                </thead>
                <tbody>
                  {this.state.data &&
                    this.state.data.map((user) => (
                      <tr key={user.id}>
                        <td>{user.id}</td>
                        <td>{user.firstName}</td>
                        <td>{user.lastName}</td>
                        <td>{user.login}</td>
                        <td>{user.cin}</td>
                        <td>{user.tel}</td>
                        <td>{user.adresse}</td>
                        <td>{user.role}</td> 
                        <td>
                        {/* <button className='btn btn-outline-danger mx-2' onClick={()=>deleteUser(user.id)}>delete</button> */}
</td>                      
                      </tr>
                    ))}
                </tbody>
              </table>
              )}
{role !=="1" && (
              <h1>Your not admin Stop!</h1>
)}
            </div>
          </div>
        </div>
      </div>
    );
    }
}
