import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { request, setAuthHeader } from '../helpers/axios_helper';

class Factures extends Component {
  constructor(props) {
    super(props);
    this.state = {
      factures: [],
      data: null,
    };
  }

  componentDidMount() {
    this.loadDataFromMessages();
    this.loadFactures();
  }

  loadDataFromMessages = () => {
    try {
      // Retrieve cin from local storage
      const cinFromLocalStorage = window.localStorage.getItem('cin');

      request("GET", `facture/${cinFromLocalStorage}`, {})
        .then((response) => {
          this.setState({ data: response.data });
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            setAuthHeader(null);
          } else {
            this.setState({ data: error.response ? error.response.data : 'Error occurred' });
          }
        });
    } catch (error) {
      console.error('Error loading data from messages:', error);
    }
  };

  loadFactures = async () => {
    try {
      // Retrieve cin from local storage
      const cinFromLocalStorage = window.localStorage.getItem('cin');

      const result = await axios.get(`http://localhost:8080/facture/${cinFromLocalStorage}`);
      this.setState({ factures: result.data });
    } catch (error) {
      console.error('Error loading factures:', error);
    }
  };

  render() {
    const { factures } = this.state;

    return (
      <div className='container'>
        <div className='py-4'>
          <table className='table border shadow'>
            <thead>
              <tr>
                <th scope='col'>Factures</th>
                <th scope='col'>Reference</th>
                <th scope='col'>montant</th>
                <th scope='col'>etat</th>
                <th scope='col'>Date</th>
                <th scope='col'>Police</th>
                <th scope='col'>Action</th>
              </tr>
            </thead>
            <tbody>
              {factures.map((facture, index) => (
                <tr key={index}>
                  <th scope='row'>{index + 1}</th>
                  <td>{facture.numFacture}</td>
                  <td>{facture.montant}</td>
                  <td>{facture.etat}</td>
                  <td>{facture.dateFacture}</td>
                  <td>{facture.numPolice}</td>
                  <td>
                    <Link className='btn btn-outline-primary mx-2' to={`/ViewFacture/${facture.numFacture}`}>
                      view
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default Factures;
