// AppContent.js
import * as React from 'react';
import { request, setAuthHeader, getAuthToken } from '../helpers/axios_helper';
import { Switch, Route, Redirect } from 'react-router-dom';
import NavBar from '../Layout/NavBar';
import FacturesContent from './Factures';
import WelcomeContent from './WelcomeContent';
import LoginForm from './LoginForm';

export default class AppContent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isAuthenticated: Boolean(getAuthToken()),
            componentToShow: "welcome",
        };
    }

    login = () => {
        this.setState({ componentToShow: "login" });
    };

    logout = () => {
        this.setState({ componentToShow: "welcome", isAuthenticated: false });
        setAuthHeader(null);
    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        request(
            "POST",
            "/login",
            {
                login: username,
                password: password
            }).then(
                (response) => {
                    setAuthHeader(response.data.token);
                    window.localStorage.setItem('cin', response.data.cin);
                    this.setState({ isAuthenticated: true });
                    this.navigate('/factures');  // Redirect to /factures after successful login
                }).catch(
                    (error) => {
                        setAuthHeader(null);
                        this.setState({ componentToShow: "welcome" });
                    }
                );
    };

    // Assuming you have a method to navigate, add the following:
    navigate = (path) => {
        this.props.history.push(path);
    };

    render() {
        return (
            <>
                <NavBar login={this.login} logout={this.logout} isAuthenticated={this.state.isAuthenticated} />
                {/* Use the Switch and Route components to handle redirection */}
                <Switch>
                    <Route path="/factures" component={FacturesContent} />
                    <Route path="/login" render={(props) => <LoginForm {...props} onLogin={this.onLogin} />} />
                    <Route path="/welcome" component={WelcomeContent} />
                    {/* Redirect to /welcome if none of the above routes match */}
                    <Redirect from="/" to="/welcome" />
                </Switch>
            </>
        );
    }
}
