// AppContent.js
import React from 'react';
import { request, setAuthHeader } from '../helpers/axios_helper';
import Buttons from './Buttons';
import LoginForm from './LoginForm';
import WelcomeContent from './WelcomeContent';
import FacturesContent from './Factures';
import ReclamationContent from './Reclamation';
import NavBar from '../Layout/NavBar'; // Import NavBar

export default class AppContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      componentToShow: 'welcome',
      isLoggedIn: false,
    };
  }

  // ... (other methods)

  onLogin = (e, username, password) => {
    e.preventDefault();
    request('POST', '/login', { login: username, password: password })
      .then((response) => {
        setAuthHeader(response.data.token);
        window.localStorage.setItem('cin', response.data.cin);
        this.setState({ componentToShow: 'Factures', isLoggedIn: true });
      })
      .catch((error) => {
        setAuthHeader(null);
        this.setState({ componentToShow: 'welcome', isLoggedIn: false });
      });
  };

  // ... (other methods)

  render() {
    return (
      <>
        <Buttons login={this.login} logout={this.logout} />
        {this.state.isLoggedIn && <NavBar onLogout={this.logout} />} {/* Conditionally render NavBar */}
        {this.state.componentToShow === 'Factures' && <FacturesContent />}
        {this.state.componentToShow === 'welcome' && <WelcomeContent />}
        {this.state.componentToShow === 'login' && (
          <LoginForm onLogin={this.onLogin} onRegister={this.onRegister} />
        )}
      </>
    );
  }
}
