import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {Link , useParams } from 'react-router-dom';
import { getAuthToken } from '../helpers/axios_helper';


// ... (previous imports)

export default function Factures() {
  const [factures, setFactures] = useState([]);
  const { cin: cinFromParams } = useParams(); // Assuming you have a route parameter for cin

  useEffect(() => {
    loadFactures();
  }, []);

  const loadFactures = async () => {
    try {
      const authToken = getAuthToken();

      // Decode the JWT token to get the user information
      const decodedToken = decodeAuthToken(authToken);
      const cinFromToken = decodedToken.cin;

      // If you have a route parameter for cin, use that instead
      const finalCin = cinFromParams || cinFromToken;

      const result = await axios.get(`http://localhost:8080/facture/${finalCin}`);
      setFactures(result.data);
    } catch (error) {
      console.error('Error loading factures:', error);
    }
  };

  // Return statement should be part of the function body
  return (
    <div className='container'>
      <div className='py-4'>
        <table className="table border shadow">
          <thead>
            <tr>
              <th scope="col">Factures</th>
              <th scope="col">Reference</th>
              <th scope="col">montant</th>
              <th scope="col">etat</th>
              <th scope="col">Date</th>
              <th scope="col">Police</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {factures.map((facture, index) => (
              <tr key={index}>
                <th scope="row">{index + 1}</th>
                <td>{facture.numFacture}</td>
                <td>{facture.montant}</td>
                <td>{facture.etat}</td>
                <td>{facture.dateFacture}</td>
                <td>{facture.numPolice}</td>
                <td>
                  <Link className='btn btn-outline-primary mx-2' to={`/ViewFacture/${facture.numFacture}`}>view</Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

// Add a function to decode the JWT token
const decodeAuthToken = (token) => {
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
    return JSON.parse(jsonPayload);
  } catch (error) {
    console.error('Error decoding token:', error);
    return {};
  }
};
